mapper.select:

SELECT COUNT(*) as cnt
FROM ATTENDANCE
WHERE TO_CHAR(inday,'YYYY-MM-DD') >=#{st, jdbcType=VARCHAR} AND TO_CHAR(inday,'YYYY-MM-DD') <=#{et, jdbcType=VARCHAR}


SELECT * 
	FROM (SELECT num, id, inday, intime, outtime, instate, 
	             outstate, rowNum rNum  
	        FROM (
	               SELECT * FROM attendance
	                ORDER BY num   
	             )  
	      ) 
	WHERE rNum >= #{start} AND rNum <= #{end}


SELECT * 
	FROM (SELECT num, id, inday, intime, outtime, instate, 
	             outstate, rowNum rNum  
	        FROM (
	               SELECT * FROM attendance
	                ORDER BY num   
	             )  
	      ) 
	WHERE rNum >= #{start} AND rNum <= #{end}
AND TO_CHAR(inday,'YYYY-MM-DD') >=#{st, jdbcType=VARCHAR} AND TO_CHAR(inday,'YYYY-MM-DD') <=#{et, jdbcType=VARCHAR}
